import pandas as pd
import matplotlib.pyplot as plt

# Read the CSV file
df = pd.read_csv('6apple_products.csv')

# Extract iPhone model from Product Name (e.g., "iPhone 12", "iPhone 11", "iPhone SE")
def extract_model(product_name):
    if 'iPhone 12 Pro Max' in product_name:
        return 'iPhone 12 Pro Max'
    elif 'iPhone 12 Pro' in product_name:
        return 'iPhone 12 Pro'
    elif 'iPhone 12 Mini' in product_name:
        return 'iPhone 12 Mini'
    elif 'iPhone 12' in product_name:
        return 'iPhone 12'
    elif 'iPhone 11 Pro Max' in product_name:
        return 'iPhone 11 Pro Max'
    elif 'iPhone 11 Pro' in product_name:
        return 'iPhone 11 Pro'
    elif 'iPhone 11' in product_name:
        return 'iPhone 11'
    elif 'iPhone XS Max' in product_name:
        return 'iPhone XS Max'
    elif 'iPhone XR' in product_name:
        return 'iPhone XR'
    elif 'iPhone SE' in product_name:
        return 'iPhone SE'
    elif 'iPhone 8 Plus' in product_name:
        return 'iPhone 8 Plus'
    elif 'iPhone 8' in product_name:
        return 'iPhone 8'
    else:
        return 'Other'

df['Model'] = df['Product Name'].apply(extract_model)

# Group by model and calculate average sale price
model_avg_price = df.groupby('Model')['Sale Price'].mean().sort_values(ascending=True)

# Create horizontal bar chart
fig, ax = plt.subplots(figsize=(12, 8))
bars = ax.barh(model_avg_price.index, model_avg_price.values, color='#555555', edgecolor='black', linewidth=0.7)

# Color bars with gradient based on price
colors = plt.cm.viridis(model_avg_price.values / model_avg_price.values.max())
for bar, color in zip(bars, colors):
    bar.set_color(color)

# Formatting
ax.set_xlabel('Average Sale Price (₹)', fontsize=12, fontweight='bold')
ax.set_ylabel('iPhone Model', fontsize=12, fontweight='bold')
ax.set_title('Average Sale Price by iPhone Model', fontsize=16, fontweight='bold', pad=20)
ax.grid(axis='x', alpha=0.3, linestyle='--')

# Add value labels on bars
for i, (model, price) in enumerate(model_avg_price.items()):
    ax.text(price + 1000, i, f'₹{price:,.0f}', va='center', fontsize=9, fontweight='bold')

plt.tight_layout()
plt.savefig('apple_products_chart.png', dpi=300, bbox_inches='tight')
plt.show()
